/**
 * Class Icon
 * Holds all the icons as base64 strings.
 *
 * @function getIcon
 */
class Icon
{
	private static $icons = array(
		'favicon' => '',
		'download' => '',
		'edit' => '',
		'delete' => '',
		'copy' => '',
		'rename' => '',
		'zip' => '',
		'loading' => '',
		'paste' => '',
		'folder' => '',
		'file' => ''
	);
	
	/**
	 * Returns the base64 string of the selected icon
	 *
	 * @param $_icon string
	 * @return string
	 */
	static function getIcon($_icon)
	{
		return self::$icons[$_icon];
	}
}

