	if (!isset($_GET['menu'])) $_GET['menu'] = '-';
	switch ($_GET['menu']) 
	{
		/*
		 * Displays server information
		 */
		case 'server':
			$_TITLE = 'Serverinfo';
			$_CONTENT = Html::getServerInfo();
			break;

		/*
		 * Logs user out
		 */
		case 'logout':
