                    var href = '?ajax=shell';
                    // Runs predefined command from dropdown menu
                    if ($('#precommand').val() != 'manual') {
                        var formData = {
                            shellcommand: $('#precommand').val()
                        };
                    }
                    // Runs a command entered by the user
                    else {
                        var formData = {
