                        url: href,
                        data: formdata
                    }).done(function(data) {
						$('#data-column').fadeIn(200);
                        data = jQuery.parseJSON(data);
                        $('#sqlresult').append(data.answer);
						var counter = 0;
                        var rows = data.rows;
						$.each(rows, function(key, value) {
							$('#data-table tbody').append('<tr id="row-'+key+'">');
							$.each(value, function(index, val) {
								
								if (counter == 0) {
